SELECT Format([PayrollPeriod],"yyyy/mmm") AS Period, PayrollReconciliation.Id, PayrollReconciliation.PayrollPeriod, PayrollReconciliation.DivisionId, PayrollReconciliation.InvoiceNumber, PayrollReconciliation.EmployeeId, PayrollReconciliation.NetPayrollEscrow, PayrollReconciliation.PayrollTaxEscrow, PayrollReconciliation.NetPayrollActual, PayrollReconciliation.PayrollTaxActual, PayrollReconciliation.NetPayrollBalance, PayrollReconciliation.PayrollTaxBalance, PayrollReconciliation.Comments, PayrollReconciliation.IsLocked, PayrollReconciliation.UpdatedAt, PayrollReconciliation.UpdatedBy
FROM PayrollReconciliation;
