SELECT [Collections-Splitted].CollectionStubId, [Collections-Splitted].CollectionId, [Collections-Splitted].CollectionDate, [Collections-Splitted].EmployeeId, [Collections-Splitted].LocationId, EquipmentType.EquipmentTypeName, Equipments.SerialNumber, [Collections-Splitted].EquipmentId, [Collections-Splitted].TransactionType, [Collections-Splitted].Split, [Collections-Splitted].SplittedAmount, GrossCashSplit([CollectionStubId],[LocationId]) AS GrossCashSplit, TotalEletronicCollections([CollectionStubId],[LocationId]) AS TotalEletronicCollections, FeeReimbursement([CollectionStubId],[LocationId]) AS FeeReimbursement, UnionVendingFees([CollectionStubId]) AS UnionVendingFees, CashToLocation([CollectionStubId]) AS CashToLocation, CashToUnionVending([CollectionStubId]) AS CashToUnionVending
FROM EquipmentType INNER JOIN ([Collections-Splitted] INNER JOIN Equipments ON [Collections-Splitted].EquipmentId = Equipments.ID) ON EquipmentType.Id = Equipments.EquipmentTypeId;
