SELECT [Collections-Splitted].CollectionStubId, [Collections-Splitted].CollectionId, Locations.LocationName, [Collections-Splitted].CollectionDate, Employees.FullName, EquipmentType.EquipmentTypeName, Equipments.SerialNumber, [Collections-Splitted].TransactionType, [Collections-Splitted].Amount, [Collections-Splitted].Split, [Collections-Splitted].SplittedAmount, [CustomFee]+[CreditCardFeeAmount]+[ServiceFeeAmount]+[LocationFeeAmount] AS ExpenseFeesTotal, [Collections-Splitted].LocationFeeAmount, [Collections-Splitted].ServiceFeeAmount, [Collections-Splitted].IsApproved
FROM EquipmentType INNER JOIN ((Locations INNER JOIN ([Collections-Splitted] INNER JOIN Equipments ON [Collections-Splitted].EquipmentId = Equipments.ID) ON Locations.id = [Collections-Splitted].LocationId) INNER JOIN Employees ON [Collections-Splitted].EmployeeId = Employees.Id) ON EquipmentType.Id = Equipments.EquipmentTypeId;
