SELECT BoxInventory.ID, BoxInventory.AcquiredDate, BoxInventory.DistributedDate, BoxInventory.SameMonthSold, BoxInventory.Status, BoxInventory.Purchaser, Suppliers.Supplier, BoxInventory.InvoiceNumber, BoxInventory.SalesOrderNumber, BoxInventory.SerialNumberList, BoxInventory.SerialNumber, OrderForms.ControlKey, GameStyles.StyleName, OrderForms.FormNumber, BoxInventory.OrderFormId, BoxInventory.GamePrice, BoxInventory.TicketCount, BoxInventory.TicketValue, BoxInventory.Revenue, BoxInventory.IdealProfit, BoxInventory.PrizesPaid, BoxInventory.LocationRent, BoxInventory.LastSale, BoxInventory.InitialSupplyStockOwnerID, BoxInventory.[xxx-InitialSupplyStockOwner], BoxInventory.TransferDate, BoxInventory.CharityDistributedFor, BoxInventory.SMACSaleInvoiceNumber, BoxInventory.SaleDate, BoxInventory.NewSupplyStockOwnerID, BoxInventory.[xxx-NewSupplyStockOwner], BoxInventory.UpdatedAt, BoxInventory.UpdatedBy
FROM GameStyles INNER JOIN ((BoxInventory LEFT JOIN Suppliers ON BoxInventory.Supplier = Suppliers.Supplier) INNER JOIN OrderForms ON BoxInventory.OrderFormId = OrderForms.Id) ON GameStyles.Id = OrderForms.GameStyleId;
