SELECT Charities.CharityName, Locations.LocationName, TicketDistribution.DistributionDate, TicketDistribution.SerialNumber, TicketDistribution.GameName, TicketDistribution.LocationCheckNumber, TicketDistribution.TicketCount, TicketDistribution.TicketSupplyCost, TicketDistribution.Profit, TicketDistribution.SMACServiceFee, TicketDistribution.SMACAdminFee, [TicketSupplyCost]+[SMACServiceFee]+[SMACAdminFee] AS Total, TicketDistribution.DistributionDate AS DateRange, TicketDistribution.CharityInvoiceNumber
FROM (TicketDistribution INNER JOIN Charities ON TicketDistribution.CharityId = Charities.Id) INNER JOIN Locations ON TicketDistribution.LocationId = Locations.id;
